function Canvas(id){
	this._canvas	=	document.getElementById(id);
	
	this._width	=	this._canvas.width;
	this._height	=	this._canvas.height;
	this._context	=	this._canvas.getContext("2d");
	this.x		=	0;
	this.y		=	0;
	this._player	=	new Player(36, 27);
	this._map	=	new Map();

	
	this.drawGrid	=	function(x,y){
		var mapset	=	this._map.getMapSet(x, y, this._width, this._height);
		var xmin	=	x - Math.floor(this._width/25/2);
		var ymin	=	y - Math.floor(this._height/25/2);
		
		for(i in mapset){
			for(j in mapset[i]){
				this._context.fillStyle	=	this._map.textures[mapset[i][j]];
				this._context.fillRect((i-xmin)*25, (j-ymin)*25, 25, 25);
			}
		}
		
		this._context.fillStyle	=	"#f00";
		this._context.fillRect((x-xmin)*25, (y-ymin)*25, 25, 25);
	}
	
	this.onup		=	function(e, canvas){
		if(canvas._map.map[canvas._player.x][canvas._player.y-1] < 4){
			x	=	canvas._player.x;
			y	=	canvas._player.y;
			canvas._player.y	-=	1;
			canvas.drawGrid(x, y-1);
		}else{
			alert("You can't walk there");
		}
	}
	this.ondown		=	function(e, canvas){
		if(canvas._map.map[canvas._player.x][canvas._player.y+1] < 4){
			x	=	canvas._player.x;
			y	=	canvas._player.y;
			canvas._player.y	+=	1;
			canvas.drawGrid(x, y+1);
		}else{
			alert("You can't walk there");
		}
	}
	this.onright		=	function(e, canvas){
		if(canvas._map.map[canvas._player.x+1][canvas._player.y] < 4){
			x	=	canvas._player.x;
			y	=	canvas._player.y;
			canvas._player.x	+=	1;
			canvas.drawGrid(x+1, y);
		}else{
			alert("You can't walk there");
		}
	}
	this.onleft		=	function(e, canvas){
		if(canvas._map.map[canvas._player.x-1][canvas._player.y] < 4){
			x	=	canvas._player.x;
			y	=	canvas._player.y;
			canvas._player.x	-=	1;
			canvas.drawGrid(x-1, y);
		}else{
			alert("You can't walk there");
		}
	}
}

